import React, { Component } from 'react';

class Main extends Component {
    state = {
        container: [],
        word: []
    }

    handleInput = (e) => {
        this.setState({
            word: e.target.value
        })
    }
    addIt = () => {
        if (this.state.word !== "") {
            let myContainer = this.state.container
            myContainer.push(this.state.word)
            this.setState({
                container: myContainer,
                word: ''
            })
        }
    }

    deleteIt = (d) => {
        let myContainer = this.state.container;
        myContainer.splice(1, d)
        this.setState({
            container: myContainer
        })
    }


    render() {
        //console.log("my value", this.state.word);
        return (
            <div>
                <h3>Todos</h3>
                <form className="container" onSubmit={this.addIt}>
                    <input type="text" placeholder="insert your todos"
                        onChange={this.handleInput}
                        value={this.state.word} />
                </form>
                <button onClick={this.addIt}>Submit</button>

                {this.state.container.map((item, d) => {
                    return (
                        <div key={d}>
                            <li className="post card">{item}<button
                                onClick={() => this.deleteIt(d)}>X</button></li>
                        </div>)
                })
                }

            </div>
        )
    }
}

export default Main;
