import React, { Component } from 'react';

class Main extends Component {
    state = {
        container: [],
        word: []
    }

    handleInput = (e) => {
        this.setState({
            word: e.target.value
        })
    }
    addIt = () => {
        if (this.state.word !== "") {
            let myContainer = this.state.container;
            myContainer.push(this.state.word);
            this.setState({
                container: myContainer
            })
        }
    }


    render() {
        console.log("my value", this.container);
        return (
            <div>
                <h3>Todos</h3>
                <form className="container" onSubmit={this.addIt}>
                    <input type="text" placeholder="insert your todos"
                        onChange={this.handleInput} />
                </form>
                <button onClick={this.addIt}>Submit</button>
            </div>
        )
    }
}

export default Main;
