import React, { Component } from 'react';

class Main extends Component {
    state = {
        container: [],
        word: []
    }

    handleInput = (e) => {
        this.setState({
            word: e.target.value
        })

        if (this.state.word !== "") {
            let myContainer = this.state.container;
            myContainer.push(this.state.word);
            this.setState({
                container: myContainer
            })
        }
    }

    render() {
        console.log("my value", this.word);
        return (
            <div>
                <h3>Todos</h3>
                <form className="container">
                    <input type="text" placeholder="insert your todos"
                        onChange={this.handleInput} />
                </form>
            </div>
        )
    }
}

export default Main;
